import { useState } from 'react'
import Button from "./utils/Button";
import Display from "./utils/Display";

const App = () => {

  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [all, setAll] = useState(0);

  const Header = ({title}) => {return ( <h1>{title}</h1> )}

  const setState = (stateToSet, value) => {
      setAll(all + 1);
      stateToSet(value + 1);
  }

  const Average = ({good, bad, all}) => {
      const average = (good - bad) / all;
      return ( <Display title={'average'} value={average}/> )}

  const Positive = ({good, bad, all}) => {
      const positive = (good / all) * 100;
      return ( <Display title={'positive'} value={positive + " %"}/> )}

    const Statistics = (props) => {

      return (
        <>
            <Display title={'good'} value={good}/>
            <Display title={'neutral'} value={neutral}/>
            <Display title={'bad'} value={bad}/>
            <Display title={'all'} value={all}/>
            <Average good={good} bad={bad} all={all}/>
            <Positive good={good} all={all}/>
        </>
    )
  }


    return (
      <div>
          <Header title={'give feedback'}/>
          <Button onClick={() => setState(setGood, good)} text='good' />
          <Button onClick={() => setState(setNeutral, neutral)} text='neutral' />
          <Button onClick={() => setState(setBad, bad)} text='bad' />
          <Header title={'statistics'}/>
          <Statistics/>
      </div>
  )
}

export default App
