const Filter = ({persons, setFilteredPersons, setSearch}) => {

    const handleSearchChange = (filteredContacts) => {
        setFilteredPersons(filteredContacts);
        setSearch(true)
    }

    const filterContacts = (event) =>
    {
        const filteredContacts = persons.filter(person => {
            return person
                    .name
                    .toLowerCase()
                    .includes(event.target.value.toLowerCase()) ||
                person
                    .number
                    .includes(event.target.value.toLowerCase())
        });
        onChange(filteredContacts);
    }

    return (
        <div>
            filter shown with <input onChange={handleSearchChange}/>
        </div>
    )
}

export default Filter
