import {useState} from "react";
import Country from "./Country";
import Countries from "./Countries";

const ShowCountries = ({countries, showSingleCountry, setShowSingleCountry}) => {

    const [country, setCountry] = useState({})

    const viewCountry = (country) => {
        setCountry(country)
        setShowSingleCountry(true)
    }

    const setSingleCountry = () => {
        setShowSingleCountry(false)
        setCountry({})
    }

    const Result = () => {
        if (countries.length === 1 || showSingleCountry) { return (<Country country={country ? country : countries[0]} back={() => setSingleCountry}/>)}
        else if(countries.length < 10) { return (<Countries countries={countries} onClick={viewCountry}/>) }
        else { return (<p>Too many matches, specify another filter</p>) }
    }

    return (<Result />)
}

export default ShowCountries
