import { useState } from 'react';
import Persons from "./components/Persons";
import Filter from "./components/Filter";


const App = () => {

    const [persons, setPersons] = useState([
        { name: 'Arto Hellas', number: '040-123456', id: 1 },
        { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
        { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
        { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
    ])

    const [search, setSearch] = useState(false)
    const [filteredPersons, setFilteredPersons] = useState('')
    const [newName, setNewName] = useState('')
    const [newNumber, setNewNumber] = useState('')

    const handleNameChange = (event) => { setNewName(event.target.value) }
    const handleNumberChange = (event) => { setNewNumber(event.target.value) }
    const handleSearchChange = (filteredContacts) => {
            setFilteredPersons(filteredContacts);
            setSearch(true)
    }

    const addContact = (event) => {
        event.preventDefault()

        const contactObject = {
            name: newName,
            number: newNumber
        }

        const found = persons.some(el => el.name === newName)

        if(found){
            alert(`${newName} is already added to phonebook`)
            return;
        }

        setPersons(persons.concat(contactObject))
        setNewName('')
    }

    const contacts = search ? filteredPersons : persons;

    return (
        <div>
            <h2>Phonebook</h2>
            <Filter persons={persons} onChange={handleSearchChange} />
            <h2>add a new</h2>
            <form onSubmit={addContact}>
                <div>
                    name: <input onChange={handleNameChange} value={newName}/>
                </div>
                <div>
                    number: <input onChange={handleNumberChange} value={newNumber} />
                </div>
                <div>
                    <button type="submit">add</button>
                </div>
            </form>
            <h2>Numbers</h2>
            <Persons contacts={contacts}/>
        </div>
    )
}

export default App
