import {useState} from "react";
import Country from "./Country";
import Countries from "./Countries";

const ShowCountries = ({countries, showSingleCountry}) => {

    const [country, setCountry] = useState(countries[0])
    const [showCountry, setShowCountry] = useState(false)

    const back = () => {
        setShowCountry(false)
    }

    const viewCountry = (country) => {
        setCountry(country)
        setShowCountry(true)
    }

    const Result = () => {
        if (countries.length === 1 || showCountry) { return (<Country country={country ? country : countries[0]} back={back}/>)}
        else if(countries.length < 10) { return (<Countries countries={countries} onClick={viewCountry}/>) }
        else { return (<p>Too many matches, specify another filter</p>) }
    }

    return (<Result />)
}

export default ShowCountries
