import { useState, useEffect } from 'react'
import Countries from "./components/Countries";
import Filter from "./components/Filter";
import axios from 'axios'
import Country from "./components/Country";

const ShowCountries = ({countries}) => {

    const [country, setCountry] = useState(countries[0])
    const [showCountry, setShowCountry] = useState(false)

    const back = () => {
        setShowCountry(false)
    }

    const viewCountry = (country) => {
        setCountry(country)
        setShowCountry(true)
    }

    const Result = () => {
        country === undefined ?? setCountry(countries[0])
        if (countries.length === 1 || showCountry) { return (<Country country={country} back={back}/>)}
        else if(countries.length < 10) { return (<Countries countries={countries} onClick={viewCountry}/>) }
        else { return (<p>Too many matches, specify another filter</p>) }
    }

    return (<Result />)
}

const App = () => {
    const [countries, setCountries] = useState([])
    const [filteredCountries, setFilteredCountries] = useState([])

    const hook = () => { axios.get('https://restcountries.com/v2/all').then(response => { setCountries(response.data) }) }
    useEffect(hook, [])

    return (
        <div>
            <Filter countries={countries} setFilteredCountries={setFilteredCountries} />
            <ShowCountries countries={filteredCountries} />
        </div>
    )
}

export default App
