import { useState, useEffect } from 'react'
import ShowCountries from "./components/ShowCountries";
import Filter from "./components/Filter";
import axios from 'axios'


const App = () => {
    const [countries, setCountries] = useState([])
    const [showSingleCountry, setShowSingleCountry] = useState(true)
    const [filteredCountries, setFilteredCountries] = useState([])

    const hook = () => { axios.get('https://restcountries.com/v2/all').then(response => { setCountries(response.data) }) }
    useEffect(hook, [])

    return (
        <div>
            <Filter countries={countries} setFilteredCountries={setFilteredCountries} back={() => setShowSingleCountry(false)} />
            <ShowCountries countries={filteredCountries} showSingleCountry={showSingleCountry} setShowSingleCountry={setShowSingleCountry} />
        </div>
    )
}

export default App
