import { useState, useEffect } from 'react'
import Persons from "./components/Persons";
import Filter from "./components/Filter";
import Form from "./components/Form";
import personsService from "./services/persons";

const App = () => {

    const [persons, setPersons] = useState([])
    const [search, setSearch] = useState(false)
    const [filteredPersons, setFilteredPersons] = useState([])
    const contacts = search ? filteredPersons : persons;

    const RemoveContact = (event) => {
        console.log(event.target.value)
        personsService.remove(event.target.value)
            .then(setPersons(persons.filter(n => n.id !== event.target.value)))
    }

    const hook = () => {
        personsService.getAll()
            .then(initialPersons => { setPersons(initialPersons) })
    }
    useEffect(hook, [])

    return (
        <div>
            <h2>Phonebook</h2>
            <Filter persons={persons} setFilteredPersons={setFilteredPersons} setSearch={setSearch} />
            <h2>add a new</h2>
            <Form persons={persons} setPersons={setPersons}/>
            <h2>Numbers</h2>
            <Persons contacts={contacts} removeContact={() => RemoveContact}/>
        </div>
    )
}

export default App
