import {useState} from "react";
import personsService from "../services/persons";

const Form = ({persons, setPersons}) => {

    const [newName, setNewName] = useState('')
    const [newNumber, setNewNumber] = useState('')

    const resetNameNumberState = () =>
    {
        setNewName('')
        setNewNumber('')
    }

    const handleNameChange = (event) => { setNewName(event.target.value) }
    const handleNumberChange = (event) => { setNewNumber(event.target.value) }

    const addContact = (event) => {
        event.preventDefault()

        const contactObject = {
            name: newName,
            number: newNumber
        }

        const found = persons.some(el => {
            return el.name === newName ? el.id : false
        })

        if(found){
            console.log(found)
            if (window.confirm(`${newName} is already added to phonebook, replace old number with a new one?`)) {
                personsService.update(found, contactObject).then(returnedPerson => {
                    setPersons(persons.map(person => person.id !== returnedPerson.id ? person : returnedPerson))
                    resetNameNumberState()
                })
                return;
            }
        }

        personsService.create(contactObject).then(returnedPerson => {
            setPersons(persons.concat(returnedPerson))
            resetNameNumberState()
        })
    }

    return(
        <form onSubmit={addContact}>
            <div>
                name: <input onChange={handleNameChange} value={newName}/>
            </div>
            <div>
                number: <input onChange={handleNumberChange} value={newNumber} />
            </div>
            <div>
                <button type="submit">add</button>
            </div>
        </form>
    )
}

export default Form;
