const Form = (props) => {

    const addContact = (event) => {
        event.preventDefault()

        const contactObject = {
            name: props.newName,
            number: props.newNumber
        }

        const found = props.persons.some(el => el.name === props.newName)

        if(found){
            alert(`${props.newName} is already added to phonebook`)
            return;
        }

        props.setPersons(props.persons.concat(contactObject))
        props.setNewName('')
    }

    return(
        <form onSubmit={addContact}>
            <div>
                name: <input onChange={props.handleNameChange} value={props.newName}/>
            </div>
            <div>
                number: <input onChange={props.handleNumberChange} value={props.newNumber} />
            </div>
            <div>
                <button type="submit">add</button>
            </div>
        </form>
    )
}

export default Form;
