import { createSlice } from '@reduxjs/toolkit'

let timeoutsIds = [];

const notificationSlice = createSlice({
    name: 'notification',
    initialState: [],
    reducers: {
        show(state, action) {
            state.message = action.payload
            state.message = action.payload
            return state
        },
        reset(state, action) {
            return state = ''
        },
    }
})

const clearTimeoutsQueue = async () => {
    if(timeoutsIds.length > 1) {
        timeoutsIds.forEach((id) => {
            if(timeoutsIds[timeoutsIds.length - 1] !== id){
                clearTimeout(id)
            }
        })
    }
}

export const { show, reset } = notificationSlice.actions

export const setNotification = (content, timer) => {
    return dispatch => {
        const timeoutId = setTimeout(() => {
            dispatch(reset())
        }, timer)
        const notification =  {
            id: timeoutId,
            message: content
        }
        dispatch(show(notification))

        timeoutsIds.push(timeoutId)
        clearTimeoutsQueue()
    }
}
export default notificationSlice.reducer
