import anecdoteService from '../services/anecdotes'
import {createSlice} from "@reduxjs/toolkit";

const anecdoteSlice = createSlice({
    name: 'anecdotes',
    initialState: [],
    reducers: {
        upvote(state, action) {
            state.filter(anecdote => {
                anecdote.votes = anecdote.id === action.payload.id ?? action.payload
                return anecdote
            })
        },
        create(state, action) {
            state.push(action.payload)
        },
        set(state, action) {
            return action.payload
        }
    },
})

export const { create, upvote, set } = anecdoteSlice.actions

export const initializeAnecdotes = () => {
    return async dispatch => {
        const anecdotes = await anecdoteService.getAll()
        dispatch(set(anecdotes))
    }
}

export const createNewAnecdote = content => {
    return async dispatch => {
        const newAnecdote = await anecdoteService.createNew(content)
        dispatch(create(newAnecdote))
    }
}

export const upvoteAnecdote = id => {
    return async dispatch => {
        await anecdoteService.upvote(id).then((id) => {
            dispatch(upvote(id))
        })
    }
}
export default anecdoteSlice.reducer
