import { useSelector } from "react-redux";

const Notification = () => {
    const notification = useSelector(({notification}) => {
        return notification.message
    })

    const style = {
        border: 'solid',
        padding: 10,
        borderWidth: 1
    }

    if (notification) {
        return (
            <div style={ style }>
                { notification }
            </div>
        )
    }
}

export default Notification
